import { Component, OnInit } from '@angular/core';
import { DataService } from "../data.service";
import { NgForm } from '@angular/forms';
import { FormGroup, FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.scss']
})
export class CreateComponent implements OnInit {
  constructor(private dataService : DataService) {}

  ngOnInit(): void {

    this.form = new FormGroup({
      name:  new FormControl('', [ Validators.required, Validators.pattern('^[a-zA-ZÁáÀàÉéÈèÍíÌìÓóÒòÚúÙùÑñüÜ \-\']+') ]),
      email: new FormControl('', [ Validators.required, Validators.email ]),
      phone: new FormControl('', [ Validators.required, Validators.pattern("^[0-9]*$") ])
    });

  }

  onSubmit(form: NgForm) {
    if (form.valid) {
      this.dataService.addItem(form.value);
      // this.addItem(form.value);
      // Your form data is valid, and you can submit it here.
      console.log(form.value);
    } else {
      // Handle form validation errors.
      // You can access form.errors and form.controls to get specific error details.
    }
  }

  addItem(newItem : any) {
    this.dataService.addItem(newItem);
  }

}
<form #myForm="ngForm" (ngSubmit)="onSubmit(myForm)">
    <div class="form-group">
        <label for="name">Name:</label>
        <input type="text" id="name" name="name" ngModel required>
        <div *ngIf="myForm.controls['name'].touched && myForm.controls['name'].invalid" class="error">
            Name is required.
        </div>
    </div>

    <div class="form-group">
        <label for="description">Description:</label>
        <input type="textarea" id="description" name="description" ngModel required>
        <div *ngIf="myForm.controls['description'].touched && myForm.controls['description'].invalid" class="error">
            Description is required.
        </div>
    </div>

    <button type="submit" [disabled]="myForm.invalid">Submit</button>
</form>
