import { Component } from '@angular/core';
import { DataService } from '../data.service';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-update',
  templateUrl: './update.component.html',
  styleUrls: ['./update.component.scss']
})
export class UpdateComponent {
  data: any;
  constructor(private dataService : DataService, private router: Router, private route: ActivatedRoute) {}

  ngOnInit(): void {
    this.dataService.findItem(this.route.snapshot.params['id']).then((response: any) => {
      this.data = response.data;
    });
  }

  onSubmit(form: NgForm) {
    if (form.valid) {
      this.dataService.updateItem(this.route.snapshot.params['id'], form.value).then((response: any) => {
        if (response) {
          this.router.navigateByUrl('item');
        }
      });;
    } else {
      // Handle form validation errors.
      // You can access form.errors and form.controls to get specific error details.
    }
  }
}
<div class="container-fluid">
    <div class="row">
        <div class="col-md-6">
            <h2>Update Item</h2>
        </div>
        <div class="col-md-6">
            <a href="#" routerLink="/item" class="btn btn-primary">Back</a>
        </div>
    </div>
    <form #myForm="ngForm" (ngSubmit)="onSubmit(myForm)">
        <div class="row">
            <div class="col-md-2">
                <label for="name">Name:</label>
            </div>
            <div class="col-md-8">
                <div class="form-group">
                    <input type="text" class="form-control" id="name" name="name" ngModel required [(ngModel)]="data.name">
                    <div *ngIf="myForm.controls['name'].touched && myForm.controls['name'].invalid" class="alert alert-danger">
                        Name is required.
                    </div>
                </div>
            </div>
        </div>
        <div class="row">
            <div class="col-md-2">
                <label for="description">Description:</label>
            </div>
            <div class="col-md-8">
                <div class="form-group">
                    <textarea id="description" class="form-control" name="description" ngModel required [(ngModel)]="data.description"></textarea>
                    <div *ngIf="myForm.controls['description'].touched && myForm.controls['description'].invalid" class="alert alert-danger">
                        Description is required.
                    </div>
                </div>
            </div>
        </div>

        <div class="row">
            <div class="col-md-2"></div>
            <div class="col-md-8">
                <button class="btn btn-primary" type="submit" [disabled]="myForm.invalid">Submit</button>
            </div>
        </div>
    </form>
</div>
